const Settings: React.FC = () => {
  const { language, setLanguage } = useLanguage();
  const { user } = useAuth();
  const [activeSection, setActiveSection] = useState<string | null>(null);

  const handleLanguageChange = (newLanguage: 'en' | 'my') => {
