  const settingsItems = [
    {
      id: 'profile',
      title: language === 'en' ? 'Profile Settings' : '·Äï·Äõ·Ä≠·ÄØ·Äñ·Ä≠·ÄØ·ÄÑ·Ä∫ ·ÄÜ·ÄÄ·Ä∫·Äê·ÄÑ·Ä∫·Äô·Äª·Ä¨·Ä∏',
      icon: User,
      description: language === 'en' ? 'Manage your account information' : '·Äû·ÄÑ·Ä∑·Ä∫·Ä°·ÄÄ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫ ·Ä°·ÄÅ·Äª·ÄÄ·Ä∫·Ä°·Äú·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ ·ÄÖ·ÄÆ·Äô·Ä∂·Äï·Ä´',
      content: (
        <div className="space-y-4">
          <div className="p-4 bg-gray-800/30 rounded-lg">
            <div className="flex items-center space-x-4">
              <div className="w-16 h-16 bg-gradient-to-r from-cyan-500 to-blue-500 rounded-full flex items-center justify-center">
                <User className="w-8 h-8 text-white" />
              </div>
              <div>
                <h3 className="text-white font-semibold">{user?.email || 'User'}</h3>
                <p className="text-gray-400 text-sm">{language === 'en' ? 'Verified Account' : '·Ä°·Äê·Ää·Ä∫·Äï·Äº·ÄØ·Äë·Ä¨·Ä∏·Äû·Ä±·Ä¨ ·Ä°·ÄÄ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫'}</p>
              </div>
            </div>
          </div>
          <div className="text-gray-300 text-sm">
            {language === 'en' 
              ? 'Profile editing is available in the navigation bar profile section.' 
              : '·Äï·Äõ·Ä≠·ÄØ·Äñ·Ä≠·ÄØ·ÄÑ·Ä∫ ·Äê·Ää·Ä∫·Ä∏·Äñ·Äº·Äê·Ä∫·ÄÅ·Äº·ÄÑ·Ä∫·Ä∏·ÄÄ·Ä≠·ÄØ navigation bar ·Äõ·Äæ·Ä≠ profile ·Ä°·Äï·Ä≠·ÄØ·ÄÑ·Ä∫·Ä∏·Äê·ÄΩ·ÄÑ·Ä∫ ·Äõ·Äõ·Äæ·Ä≠·Äî·Ä≠·ÄØ·ÄÑ·Ä∫·Äï·Ä´·Äû·Ää·Ä∫·Åã'
            }
          </div>
        </div>
      )
    },
    {
      id: 'language',
      title: language === 'en' ? 'Language Settings' : '·Äò·Ä¨·Äû·Ä¨·ÄÖ·ÄÄ·Ä¨·Ä∏ ·ÄÜ·ÄÄ·Ä∫·Äê·ÄÑ·Ä∫·Äô·Äª·Ä¨·Ä∏',
      icon: Globe,
      description: language === 'en' ? 'Choose your preferred language' : '·Äû·ÄÑ·Ä∫·Äî·Äæ·ÄÖ·Ä∫·Äû·ÄÄ·Ä∫·Äû·Ä±·Ä¨ ·Äò·Ä¨·Äû·Ä¨·ÄÖ·ÄÄ·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ ·Äõ·ÄΩ·Ä±·Ä∏·ÄÅ·Äª·Äö·Ä∫·Äï·Ä´',
      content: (
        <div className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => handleLanguageChange('en')}
              className={`p-4 rounded-lg border-2 transition-all ${
                language === 'en'
                  ? 'border-cyan-400 bg-cyan-500/20 text-cyan-300'
                  : 'border-gray-600 bg-gray-800/30 text-gray-300 hover:border-gray-500'
              }`}
            >
              <div className="text-left">
                <div className="font-semibold">English</div>
                <div className="text-sm opacity-70">Default language</div>
              </div>
            </motion.button>

            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => handleLanguageChange('my')}
              className={`p-4 rounded-lg border-2 transition-all ${
                language === 'my'
                  ? 'border-cyan-400 bg-cyan-500/20 text-cyan-300'
                  : 'border-gray-600 bg-gray-800/30 text-gray-300 hover:border-gray-500'
              }`}
            >
              <div className="text-left">
                <div className="font-semibold">·Äô·Äº·Äî·Ä∫·Äô·Ä¨</div>
                <div className="text-sm opacity-70">Burmese</div>
              </div>
            </motion.button>
          </div>
        </div>
      )
    },
    {
      id: 'feedback',
      title: language === 'en' ? 'Feedback' : '·Ä°·ÄÄ·Äº·Ä∂·Äï·Äº·ÄØ·ÄÅ·Äª·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏',
      icon: MessageSquare,
      description: language === 'en' ? 'Share your thoughts and suggestions' : '·Äû·ÄÑ·Ä∑·Ä∫·Ä°·Äê·ÄΩ·Ä±·Ä∏·Äô·Äª·Ä¨·Ä∏·Äî·Äæ·ÄÑ·Ä∑·Ä∫ ·Ä°·ÄÄ·Äº·Ä∂·Äï·Äº·ÄØ·ÄÅ·Äª·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ ·Äô·Äª·Äæ·Äù·Ä±·Äï·Ä´',
      content: (
        <div className="space-y-4">
          <div className="p-4 bg-gradient-to-r from-cyan-500/10 to-blue-500/10 rounded-lg border border-cyan-500/20">
            <h4 className="text-white font-semibold mb-2">
              {language === 'en' ? 'Help Us Improve' : '·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Äê·Ä≠·ÄØ·Ä∑·ÄÄ·Ä≠·ÄØ ·Äê·Ä≠·ÄØ·Ä∏·Äê·ÄÄ·Ä∫·Ä°·Ä±·Ä¨·ÄÑ·Ä∫ ·ÄÄ·Ä∞·Ää·ÄÆ·Äï·Ä´'}
            </h4>
            <p className="text-gray-300 text-sm mb-4">
              {language === 'en' 
                ? 'Your feedback is valuable to us. Share your experience and help us make SateCha better.' 
                : '·Äû·ÄÑ·Ä∑·Ä∫·Ä°·ÄÄ·Äº·Ä∂·Äï·Äº·ÄØ·ÄÅ·Äª·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏·Äû·Ää·Ä∫ ·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Äê·Ä≠·ÄØ·Ä∑·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Ä°·Äñ·Ä≠·ÄØ·Ä∏·Äê·Äî·Ä∫·Äï·Ä´·Äû·Ää·Ä∫·Åã'
              }
            </p>
            <div className="flex space-x-3">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowFeedbackForm(true)}
                className="px-6 py-3 bg-gradient-to-r from-cyan-500 to-blue-500 text-white rounded-lg hover:from-cyan-600 hover:to-blue-600 transition-all font-medium"
              >
                {language === 'en' ? 'Give Feedback' : '·Ä°·ÄÄ·Äº·Ä∂·Äï·Äº·ÄØ·ÄÅ·Äª·ÄÄ·Ä∫ ·Äï·Ä±·Ä∏·Äï·Ä´'}
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowFeedbackViewer(true)}
                className="px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-600 hover:to-pink-600 transition-all font-medium"
              >
                {language === 'en' ? 'View My Feedback' : '·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Åè ·Ä°·ÄÄ·Äº·Ä∂·Äï·Äº·ÄØ·ÄÅ·Äª·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏ ·ÄÄ·Äº·Ää·Ä∑·Ä∫·Äõ·Äæ·ÄØ·Äï·Ä´'}
              </motion.button>
            </div>
          </div>
        </div>
      )
    },
    {
      id: 'terms',
      title: language === 'en' ? 'Terms & Conditions' : '·ÄÖ·Ää·Ä∫·Ä∏·Äô·Äª·Äâ·Ä∫·Ä∏·Äô·Äª·Ä¨·Ä∏',
      icon: FileText,
      description: language === 'en' ? 'Review our terms of service' : '·Äù·Äî·Ä∫·ÄÜ·Ä±·Ä¨·ÄÑ·Ä∫·Äô·Äæ·ÄØ ·ÄÖ·Ää·Ä∫·Ä∏·Äô·Äª·Äâ·Ä∫·Ä∏·Äô·Äª·Ä¨·Ä∏',
      content: (
        <div className="space-y-4 max-h-96 overflow-y-auto">
          <div className="text-gray-300 text-sm space-y-3">
            <p>1. {language === 'en' ? 'By using SateCha, you agree to comply with all applicable laws.' : 'SateCha ·ÄÄ·Ä≠·ÄØ ·Ä°·Äû·ÄØ·Ä∂·Ä∏·Äï·Äº·ÄØ·ÄÅ·Äº·ÄÑ·Ä∫·Ä∏·Äñ·Äº·ÄÑ·Ä∑·Ä∫ ·Ä•·Äï·Äí·Ä±·Äô·Äª·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ ·Äú·Ä≠·ÄØ·ÄÄ·Ä∫·Äî·Ä¨·Äõ·Äî·Ä∫ ·Äû·Äò·Ä±·Ä¨·Äê·Ä∞·Äï·Ä´·Äû·Ää·Ä∫·Åã'}</p>
            <p>2. {language === 'en' ? 'We protect your privacy and secure your data.' : '·ÄÄ·Äª·ÄΩ·Äî·Ä∫·ÄØ·Äï·Ä∫·Äê·Ä≠·ÄØ·Ä∑·Äû·Ää·Ä∫ ·Äû·ÄÑ·Ä∑·Ä∫·ÄÄ·Ä≠·ÄØ·Äö·Ä∫·Äõ·Ä±·Ä∏·ÄÄ·Ä≠·ÄØ·Äö·Ä∫·Äê·Ä¨·ÄÄ·Ä≠·ÄØ ·ÄÄ·Ä¨·ÄÄ·ÄΩ·Äö·Ä∫·Äï·Ä´·Äû·Ää·Ä∫·Åã'}</p>
            <p>3. {language === 'en' ? 'Users are responsible for account security.' : '·Ä°·Äû·ÄØ·Ä∂·Ä∏·Äï·Äº·ÄØ·Äû·Ä∞·Äô·Äª·Ä¨·Ä∏·Äû·Ää·Ä∫ ·Ä°·ÄÄ·Ä±·Ä¨·ÄÑ·Ä∑·Ä∫·Äú·ÄØ·Ä∂·ÄÅ·Äº·ÄØ·Ä∂·Äõ·Ä±·Ä∏·Ä°·Äê·ÄΩ·ÄÄ·Ä∫ ·Äê·Ä¨·Äù·Äî·Ä∫·Äõ·Äæ·Ä≠·Äï·Ä´·Äû·Ää·Ä∫·Åã'}</p>
          </div>
        </div>
      )
    },
    {
      id: 'manual',
      title: language === 'en' ? 'User Manual' : '·Ä°·Äû·ÄØ·Ä∂·Ä∏·Äï·Äº·ÄØ·Äû·Ä∞ ·Äú·Äô·Ä∫·Ä∏·Ää·ÄΩ·Äæ·Äî·Ä∫',
      icon: BookOpen,
      description: language === 'en' ? 'Learn how to use SateCha' : 'SateCha ·Ä°·Äû·ÄØ·Ä∂·Ä∏·Äï·Äº·ÄØ·Äî·Ää·Ä∫·Ä∏',
      content: (
        <div className="space-y-4">
          <div className="p-4 bg-gray-800/30 rounded-lg">
            <h5 className="text-white font-semibold mb-2">üè† Dashboard</h5>
            <p className="text-gray-300 text-sm">View cybersecurity moments and incidents.</p>
          </div>
          <div className="p-4 bg-gray-800/30 rounded-lg">
            <h5 className="text-white font-semibold mb-2">üß† Quiz</h5>
            <p className="text-gray-300 text-sm">Test your knowledge with interactive quizzes.</p>
          </div>
          <div className="p-4 bg-gray-800/30 rounded-lg">
            <h5 className="text-white font-semibold mb-2">üõ†Ô∏è Tools</h5>
            <p className="text-gray-300 text-sm">Access cybersecurity tools and utilities.</p>
          </div>
        </div>
      )
    },
    {
      id: 'security',
      title: language === 'en' ? 'Security & Privacy' : '·Äú·ÄØ·Ä∂·ÄÅ·Äº·ÄØ·Ä∂·Äõ·Ä±·Ä∏',
      icon: Shield,
      description: language === 'en' ? 'Security information' : '·Äú·ÄØ·Ä∂·ÄÅ·Äº·ÄØ·Ä∂·Äõ·Ä±·Ä∏ ·Ä°·ÄÅ·Äª·ÄÄ·Ä∫·Ä°·Äú·ÄÄ·Ä∫·Äô·Äª·Ä¨·Ä∏',
      content: (
        <div className="space-y-4">
          <div className="p-4 bg-gradient-to-r from-green-500/10 to-cyan-500/10 rounded-lg border border-green-500/20">
            <h5 className="text-white font-semibold mb-2">üîê Your Data is Secure</h5>
            <ul className="text-gray-300 text-sm space-y-2">
              <li>‚Ä¢ End-to-end encryption</li>
              <li>‚Ä¢ OTP-only authentication</li>
              <li>‚Ä¢ Regular security updates</li>
              <li>‚Ä¢ GDPR compliant</li>
            </ul>
          </div>
        </div>
      )
    }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-black to-gray-900 text-white p-6">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <AnimatedText variant="glow" className="text-4xl font-bold mb-4">
            {language === 'en' ? 'Settings' : '·ÄÜ·ÄÄ·Ä∫·Äê·ÄÑ·Ä∫·Äô·Äª·Ä¨·Ä∏'}
          </AnimatedText>
          <p className="text-gray-400">
            {language === 'en' ? 'Manage your preferences' : '·Äû·ÄÑ·Ä∑·Ä∫·Äî·Äæ·ÄÖ·Ä∫·Äû·ÄÄ·Ä∫·Äô·Äæ·ÄØ·Äô·Äª·Ä¨·Ä∏·ÄÄ·Ä≠·ÄØ ·ÄÖ·ÄÆ·Äô·Ä∂·Äï·Ä´'}
          </p>
        </div>

        <div className="space-y-4">
          {settingsItems.map((item) => {
            const Icon = item.icon;
            const isActive = activeSection === item.id;

            return (
              <motion.div
