        </div>
      </div>

      <FeedbackForm
        isOpen={showFeedbackForm}
        onClose={() => setShowFeedbackForm(false)}
      />

      <FeedbackViewer
        isOpen={showFeedbackViewer}
        onClose={() => setShowFeedbackViewer(false)}
        adminMode={false}
      />
    </div>
  );
};

export default Settings;
