import React, { useState, useEffect } from 'react';
import { Plus, Search, Book, FileText, Trash2 } from 'lucide-react';
import { chatService } from '../services/api';

const KnowledgeBase = () => {
  const [knowledgeItems, setKnowledgeItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAddForm, setShowAddForm] = useState(false);
  const [newItem, setNewItem] = useState({ title: '', content: '' });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    loadKnowledgeBase();
  }, []);

  const loadKnowledgeBase = async () => {
    try {
      const response = await chatService.getKnowledgeBase();
      setKnowledgeItems(response.items || []);
    } catch (error) {
      console.error('Error loading knowledge base:', error);
    }
  };

  const handleAddKnowledge = async () => {
    if (!newItem.title.trim() || !newItem.content.trim()) return;

    setIsLoading(true);
    try {
      await chatService.addKnowledge(newItem);
      setKnowledgeItems(prev => [...prev, { ...newItem, id: Date.now() }]);
      setNewItem({ title: '', content: '' });
      setShowAddForm(false);
    } catch (error) {
      console.error('Error adding knowledge:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleDeleteItem = (id) => {
    setKnowledgeItems(prev => prev.filter(item => item.id !== id));
  };

  const filteredItems = knowledgeItems.filter(item =>
    item.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    item.content.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="h-full bg-gradient-to-br from-teal-400 via-cyan-500 to-blue-600 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-4">Knowledge Base</h1>
          <p className="text-white/80">Manage your cybersecurity knowledge and resources</p>
        </div>

        <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6 mb-6">
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <div className="flex-1 relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search knowledge base..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
              />
            </div>
            <button
              onClick={() => setShowAddForm(!showAddForm)}
              className="bg-teal-600 hover:bg-teal-700 text-white px-4 py-2 rounded-lg flex items-center space-x-2 transition-colors"
            >
              <Plus className="w-5 h-5" />
              <span>Add Knowledge</span>
            </button>
          </div>

          {showAddForm && (
            <div className="bg-white rounded-lg p-4 mb-6">
              <h3 className="text-lg font-semibold mb-4">Add New Knowledge</h3>
              <div className="space-y-4">
                <input
                  type="text"
                  placeholder="Title"
                  value={newItem.title}
                  onChange={(e) => setNewItem(prev => ({ ...prev, title: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
                />
                <textarea
                  placeholder="Content"
                  value={newItem.content}
                  onChange={(e) => setNewItem(prev => ({ ...prev, content: e.target.value }))}
                  rows="4"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
                />
                <div className="flex space-x-2">
                  <button
                    onClick={handleAddKnowledge}
                    disabled={isLoading || !newItem.title.trim() || !newItem.content.trim()}
                    className="bg-teal-600 hover:bg-teal-700 disabled:bg-gray-400 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    {isLoading ? 'Adding...' : 'Add'}
                  </button>
                  <button
                    onClick={() => setShowAddForm(false)}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded-lg transition-colors"
                  >
                    Cancel
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {filteredItems.length === 0 ? (
            <div className="col-span-full text-center py-12">
              <Book className="w-16 h-16 mx-auto text-white/50 mb-4" />
              <p className="text-white/80 text-lg">
                {searchTerm ? 'No knowledge items found matching your search.' : 'No knowledge items yet. Add some to get started!'}
              </p>
            </div>
          ) : (
            filteredItems.map((item) => (
              <div key={item.id} className="bg-white rounded-lg p-4 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-start justify-between mb-2">
                  <div className="flex items-center space-x-2">
                    <FileText className="w-5 h-5 text-teal-600" />
                    <h3 className="font-semibold text-gray-800 truncate">{item.title}</h3>
                  </div>
                  <button
                    onClick={() => handleDeleteItem(item.id)}
                    className="text-red-500 hover:text-red-700 transition-colors"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
                <p className="text-gray-600 text-sm line-clamp-3">{item.content}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default KnowledgeBase;
