import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, User, BarChart3, Settings as SettingsIcon, Wrench, LogIn, LogOut, MessageCircle } from 'lucide-react';
import './styles/globals.css';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';
import Tools from './components/Tools';
import Settings from './components/Settings';
import Profile from './components/Profile';
import Admin from './components/Admin';
import AuthModal from './components/AuthModal';
import Chatbot from './components/Chatbot';
import FloatingLogo from './components/FloatingLogo';
import Footer from './components/Footer';
import { AuthProvider, useAuth } from './contexts/AuthContext';

const AppContent: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'dashboard' | 'quiz' | 'tools' | 'settings' | 'profile' | 'admin'>('home');
  const [isLoading, setIsLoading] = useState(true);
  const [particles, setParticles] = useState<Array<{id: number, x: number, y: number, delay: number}>>([]);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const { user, signOut } = useAuth();

  useEffect(() => {
    // Generate floating particles for background
    const newParticles = Array.from({ length: 30 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      delay: Math.random() * 2
    }));
    setParticles(newParticles);

    // Simulate loading
    setTimeout(() => setIsLoading(false), 1500);
  }, []);

  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Shield },
    { id: 'quiz', label: 'Quiz', icon: BarChart3 },
    { id: 'tools', label: 'Tools', icon: Wrench },
    { id: 'chatbot', label: 'AI Chat', icon: MessageCircle },
    { id: 'settings', label: 'Settings', icon: SettingsIcon },
    { id: 'profile', label: 'Profile', icon: User },
  ];

  if (isLoading) {
    return (
      <div className="h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 flex items-center justify-center overflow-hidden cyber-grid">
        <motion.div
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          className="text-center glass-card"
        >
          <motion.div className="flex items-center justify-center mb-6">
            <motion.img
              src="/logo.png"
              alt="SateCha Logo"
              className="w-16 h-16 mr-4"
              animate={{ scale: [1, 1.05, 1] }}
              transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            />
            <div>
              <motion.h1
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.3 }}
                className="text-4xl font-bold text-white"
                style={{ fontFamily: 'Orbitron, monospace' }}
              >
                SateCha
              </motion.h1>
              <motion.p
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.5 }}
                className="text-cyan-400 text-sm"
              >
                Cybersecurity Platform
              </motion.p>
            </div>
          </motion.div>

          {/* Advanced Tech-Based Loader */}
          <div className="relative w-24 h-24 mx-auto mb-6">
            {/* Outer Ring */}
            <motion.div
              className="absolute inset-0 border-2 border-cyan-400/30 rounded-full"
              animate={{ rotate: 360 }}
              transition={{ duration: 3, repeat: Infinity, ease: "linear" }}
            />

            {/* Middle Ring */}
            <motion.div
              className="absolute inset-2 border-2 border-blue-500/50 rounded-full border-dashed"
              animate={{ rotate: -360 }}
              transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
            />

            {/* Inner Core */}
            <motion.div
              className="absolute inset-6 bg-gradient-to-r from-cyan-500 to-blue-500 rounded-full"
              animate={{
                scale: [1, 1.2, 1],
                boxShadow: [
                  "0 0 20px rgba(6, 182, 212, 0.5)",
                  "0 0 40px rgba(6, 182, 212, 0.8)",
                  "0 0 20px rgba(6, 182, 212, 0.5)"
                ]
              }}
              transition={{ duration: 1.5, repeat: Infinity, ease: "easeInOut" }}
            />

            {/* Scanning Lines */}
            {[...Array(4)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute inset-0 border border-cyan-400/20 rounded-full"
                style={{
                  transform: `scale(${1 + i * 0.3})`
                }}
                animate={{
                  opacity: [0, 0.8, 0],
                  scale: [1 + i * 0.3, 1.5 + i * 0.3, 1 + i * 0.3]
                }}
                transition={{
                  duration: 2,
                  repeat: Infinity,
                  delay: i * 0.3,
                  ease: "easeOut"
                }}
              />
            ))}

            {/* Data Particles */}
            {[...Array(8)].map((_, i) => (
              <motion.div
                key={i}
                className="absolute w-1 h-1 bg-cyan-400 rounded-full"
                style={{
                  left: `${50 + 35 * Math.cos(i * 45 * Math.PI / 180)}%`,
                  top: `${50 + 35 * Math.sin(i * 45 * Math.PI / 180)}%`
                }}
                animate={{
                  scale: [0, 1, 0],
                  opacity: [0, 1, 0]
                }}
                transition={{
                  duration: 1,
                  repeat: Infinity,
                  delay: i * 0.125,
                  ease: "easeInOut"
                }}
              />
            ))}

            {/* Central Pulse */}
            <motion.div
              className="absolute inset-8 bg-white/20 rounded-full backdrop-blur-sm"
              animate={{
                scale: [0.8, 1, 0.8],
                opacity: [0.3, 0.7, 0.3]
              }}
              transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            />
          </div>

          <motion.p
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.7 }}
            className="text-gray-300"
          >
            Initializing Security Systems...
          </motion.p>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 overflow-hidden relative cyber-grid flex flex-col">
      {/* Advanced Tech Background */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Simple Matrix Rain - 0s and 1s falling */}
        <div className="absolute inset-0">
          {[...Array(40)].map((_, i) => (
            <motion.div
              key={`matrix-${i}`}
              className="absolute text-green-400/30 text-sm font-mono"
              style={{
                left: `${i * 2.5}%`,
                top: '-10%'
              }}
              animate={{
                y: ['0vh', '110vh'],
                opacity: [0, 0.8, 0]
              }}
              transition={{
                duration: 6 + Math.random() * 4,
                repeat: Infinity,
                delay: Math.random() * 8,
                ease: "linear"
              }}
            >
              {Array.from({ length: 15 }, (_, idx) => (
                <div key={idx} className="mb-1">
                  {Math.random() > 0.5 ? '1' : '0'}
                </div>
              ))}
            </motion.div>
          ))}
        </div>

        {/* Removed horizontal line animations and circuit grid as requested */}

        {/* Holographic Nodes */}
        {[...Array(12)].map((_, i) => (
          <motion.div
            key={`node-${i}`}
            className="absolute w-2 h-2 rounded-full"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              background: 'radial-gradient(circle, rgba(6,182,212,0.8) 0%, rgba(59,130,246,0.4) 50%, transparent 100%)'
            }}
            animate={{
              scale: [0.5, 1.5, 0.5],
              opacity: [0.3, 0.8, 0.3],
              rotate: [0, 360]
            }}
            transition={{
              duration: 3 + i * 0.2,
              repeat: Infinity,
              delay: i * 0.3,
              ease: "easeInOut"
            }}
          >
            {/* Node Pulse Ring */}
            <motion.div
              className="absolute inset-0 rounded-full border border-cyan-400/30"
              animate={{
                scale: [1, 3, 1],
                opacity: [0.5, 0, 0.5]
              }}
              transition={{
                duration: 2,
                repeat: Infinity,
                delay: i * 0.2,
                ease: "easeOut"
              }}
            />
          </motion.div>
        ))}

        {/* Floating Tech Particles */}
        {particles.map((particle) => (
          <motion.div
            key={particle.id}
            className="absolute w-1 h-1"
            style={{ left: `${particle.x}%`, top: `${particle.y}%` }}
            animate={{
              y: [0, -150, 0],
              opacity: [0, 0.8, 0],
              scale: [0, 1.2, 0],
              rotate: [0, 180, 360]
            }}
            transition={{
              duration: 5,
              repeat: Infinity,
              delay: particle.delay,
              ease: "easeInOut"
            }}
          >
            <div className="w-full h-full bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full shadow-lg shadow-cyan-400/50" />
          </motion.div>
        ))}

        {/* Scanning Lines */}
        <motion.div
          className="absolute inset-0"
          animate={{
            background: [
              'linear-gradient(90deg, transparent 0%, rgba(6,182,212,0.1) 50%, transparent 100%)',
              'linear-gradient(180deg, transparent 0%, rgba(59,130,246,0.1) 50%, transparent 100%)',
              'linear-gradient(270deg, transparent 0%, rgba(139,92,246,0.1) 50%, transparent 100%)',
              'linear-gradient(0deg, transparent 0%, rgba(6,182,212,0.1) 50%, transparent 100%)'
            ]
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "linear"
          }}
        />
      </div>

      {/* Header Navigation */}
      <motion.header
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="relative z-10 glass-dark border-b border-cyan-500/20"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 py-3 sm:py-4">
          <div className="flex items-center justify-between flex-wrap gap-4">
            {/* Logo - Clickable to Home */}
            <motion.button
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.6 }}
              onClick={() => setActiveTab('home')}
              className="flex items-center space-x-3 hover:scale-105 transition-transform duration-200 cursor-pointer"
            >
              <motion.div className="relative">
                <img
                  src="/logo.png"
                  alt="SateCha Logo"
                  className="w-10 h-10 object-contain"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-lg blur-lg opacity-30 -z-10" />
              </motion.div>
              <div>
                <h1 className="text-2xl font-bold text-white" style={{ fontFamily: 'Orbitron, monospace' }}>
                  SateCha
                </h1>
                <p className="text-xs text-cyan-400">Cybersecurity Platform</p>
              </div>
            </motion.button>

            {/* Navigation */}
            <nav className="flex flex-wrap gap-1 sm:gap-2">
              {navItems.map((item, index) => {
                const Icon = item.icon;
                const gradients = [
                  'from-cyan-500 via-blue-500 to-purple-600',
                  'from-green-400 via-cyan-500 to-blue-600', 
                  'from-purple-500 via-pink-500 to-red-500',
                  'from-yellow-400 via-orange-500 to-red-600'
                ];
                return (
                  <motion.button
                    key={item.id}
                    initial={{ opacity: 0, y: -30, rotateX: -90 }}
                    animate={{ opacity: 1, y: 0, rotateX: 0 }}
                    transition={{ delay: index * 0.15 + 0.3, type: "spring", stiffness: 100, damping: 10 }}
                    whileHover={{ scale: 1.05, y: -3, transition: { duration: 0.2, ease: "easeOut" } }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => setActiveTab(item.id as any)}
                    className={`relative px-2 sm:px-3 md:px-4 py-2 sm:py-2.5 font-semibold transition-all duration-300 flex flex-col sm:flex-row items-center space-y-1 sm:space-y-0 sm:space-x-2 overflow-hidden group text-xs sm:text-sm md:text-base min-w-[60px] sm:min-w-[80px] md:min-w-auto ${
                      activeTab === item.id
                        ? 'nav-item-active'
                        : 'nav-item-inactive'
                    }`}
                  >
                    {/* Animated background for inactive buttons */}
                    {activeTab !== item.id && (
                      <motion.div
                        className={`absolute inset-0 bg-gradient-to-r ${gradients[index % gradients.length]} opacity-0 group-hover:opacity-20 transition-opacity duration-300`}
                        initial={false}
                      />
                    )}

                    {/* Icon with Technical Effects */}
                    <motion.div
                      className="relative z-10 flex items-center justify-center mb-1 sm:mb-0"
                    >
                      {/* Icon Glow */}
                      <motion.div
                        className="absolute inset-0 bg-cyan-400/50 rounded-full blur-sm"
                        animate={{
                          scale: activeTab === item.id ? [1, 1.3, 1] : 1,
                          opacity: activeTab === item.id ? [0.5, 0.8, 0.5] : 0
                        }}
                        transition={{
                          duration: 2,
                          repeat: activeTab === item.id ? Infinity : 0,
                          ease: "easeInOut"
                        }}
                      />
                      <Icon className="w-4 h-4 sm:w-5 sm:h-5 relative z-10" />
                    </motion.div>

                    {/* Label - Always visible on mobile with smaller text */}
                    <span className="relative z-10 font-bold tracking-wider text-center leading-tight">
                      {item.label}
                    </span>

                    {/* Digital Particles for Active Tab */}
                    {activeTab === item.id && (
                      <motion.div
                        className="absolute inset-0 pointer-events-none overflow-hidden"
                        initial={false}
                      >
                        {[...Array(12)].map((_, i) => (
                          <motion.div
                            key={i}
                            className="absolute w-0.5 h-0.5 bg-cyan-400 rounded-full"
                            style={{
                              left: `${Math.random() * 100}%`,
                              top: `${Math.random() * 100}%`
                            }}
                            animate={{
                              y: [-5, -15, -5],
                              x: [0, Math.random() * 10 - 5, 0],
                              opacity: [0, 1, 0],
                              scale: [0, 1.5, 0]
                            }}
                            transition={{
                              duration: 1.5 + Math.random(),
                              repeat: Infinity,
                              delay: i * 0.1,
                              ease: "easeInOut"
                            }}
                          />
                        ))}
                      </motion.div>
                    )}

                    {/* Corner Brackets for Active State */}
                    {activeTab === item.id && (
                      <>
                        <motion.div
                          className="absolute top-1 left-1 w-3 h-3 border-t-2 border-l-2 border-cyan-400"
                          initial={{ opacity: 0, scale: 0 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ duration: 0.3 }}
                        />
                        <motion.div
                          className="absolute top-1 right-1 w-3 h-3 border-t-2 border-r-2 border-cyan-400"
                          initial={{ opacity: 0, scale: 0 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ duration: 0.3, delay: 0.1 }}
                        />
                        <motion.div
                          className="absolute bottom-1 left-1 w-3 h-3 border-b-2 border-l-2 border-cyan-400"
                          initial={{ opacity: 0, scale: 0 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ duration: 0.3, delay: 0.2 }}
                        />
                        <motion.div
                          className="absolute bottom-1 right-1 w-3 h-3 border-b-2 border-r-2 border-cyan-400"
                          initial={{ opacity: 0, scale: 0 }}
                          animate={{ opacity: 1, scale: 1 }}
                          transition={{ duration: 0.3, delay: 0.3 }}
                        />
                      </>
                    )}
                  </motion.button>
                );
              })}
            </nav>

            {/* Authentication & Profile */}
            <div className="flex items-center space-x-3">
              {user ? (
                <>
                  {/* Username Display */}
                  <motion.div
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    className="hidden sm:flex items-center space-x-2 text-sm"
                  >
                    <span className="text-gray-300">Welcome,</span>
                    <span className="text-cyan-400 font-semibold font-orbitron">
                      {user.user_metadata?.username || user.email?.split('@')[0] || 'User'}
                    </span>
                  </motion.div>

                  {/* Admin Button - Only show for admin users */}
                  {user.user_metadata?.role === 'admin' && (
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => setActiveTab('admin')}
                      className="px-3 py-2 bg-gradient-to-r from-purple-500 to-pink-500 text-white rounded-lg hover:from-purple-400 hover:to-pink-400 transition-colors flex items-center space-x-2 text-sm"
                    >
                      <SettingsIcon className="w-4 h-4" />
                      <span className="hidden sm:block">Admin</span>
                    </motion.button>
                  )}

                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => setActiveTab('profile')}
                    className="relative group"
                  >
                    <div className="w-10 h-10 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full flex items-center justify-center">
                      <User className="w-5 h-5 text-black" />
                    </div>
                    <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full blur-lg opacity-0 group-hover:opacity-50 transition-opacity -z-10" />
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={signOut}
                    className="px-3 py-2 bg-red-500/20 text-red-400 border border-red-500/30 rounded-lg hover:bg-red-500/30 transition-colors flex items-center space-x-2"
                  >
                    <LogOut className="w-4 h-4" />
                    <span className="hidden sm:block text-sm">Sign Out</span>
                  </motion.button>
                </>
              ) : (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setShowAuthModal(true)}
                  className="px-4 py-2 bg-gradient-to-r from-cyan-500 to-blue-500 text-white rounded-lg hover:from-cyan-400 hover:to-blue-400 transition-colors flex items-center space-x-2 font-orbitron"
                >
                  <LogIn className="w-4 h-4" />
                  <span>Sign In</span>
                </motion.button>
              )}
            </div>
          </div>
        </div>
      </motion.header>

      {/* Main Content */}
      <main className="flex-1 overflow-auto relative z-10">
        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            initial={{ opacity: 0, x: 300, scale: 0.9 }}
            animate={{ opacity: 1, x: 0, scale: 1 }}
            exit={{ opacity: 0, x: -300, scale: 0.9 }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
            className="h-full"
          >
            {activeTab === 'home' && <Home onNavigate={setActiveTab} />}
            {activeTab === 'dashboard' && <Dashboard />}
            {activeTab === 'quiz' && <Quiz />}
            {activeTab === 'tools' && <Tools />}
            {activeTab === 'chatbot' && <Chatbot />}
            {activeTab === 'settings' && <Settings />}
            {activeTab === 'profile' && <Profile />}
            {activeTab === 'admin' && <Admin />}
          </motion.div>
        </AnimatePresence>
      </main>

      {/* Footer */}
      <Footer />

      {/* Tech Border Effects */}
      <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-cyan-400 to-transparent" />
      <div className="absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-blue-500 to-transparent" />
      <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-cyan-400 to-transparent" />
      <div className="absolute top-0 right-0 w-1 h-full bg-gradient-to-b from-transparent via-blue-500 to-transparent" />

      {/* Authentication Modal */}
      <AuthModal 
        isOpen={showAuthModal} 
        onClose={() => setShowAuthModal(false)} 
      />

      {/* 3D Floating Logo */}
      <FloatingLogo onClick={() => setActiveTab('chatbot')} />
    </div>
  );
};

// Main App component with AuthProvider
function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
