import React, { useState, useEffect } from 'react';
import { Search, Calendar, MessageSquare, Trash2, Download } from 'lucide-react';

const ChatHistory = () => {
  const [conversations, setConversations] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDate, setSelectedDate] = useState('');
  const [sortBy, setSortBy] = useState('newest');

  useEffect(() => {
    // Load mock conversation data
    const mockConversations = [
      {
        id: 1,
        title: 'Cybersecurity Best Practices',
        date: '2024-01-15',
        messageCount: 12,
        preview: 'Discussion about password security and two-factor authentication...',
        timestamp: '2024-01-15T10:30:00Z'
      },
      {
        id: 2,
        title: 'Network Security Analysis',
        date: '2024-01-14',
        messageCount: 8,
        preview: 'Analysis of network vulnerabilities and protection strategies...',
        timestamp: '2024-01-14T14:20:00Z'
      },
      {
        id: 3,
        title: 'Incident Response Planning',
        date: '2024-01-13',
        messageCount: 15,
        preview: 'Creating an effective incident response plan for organizations...',
        timestamp: '2024-01-13T09:15:00Z'
      }
    ];
    setConversations(mockConversations);
  }, []);

  const handleDeleteConversation = (id) => {
    if (window.confirm('Are you sure you want to delete this conversation?')) {
      setConversations(prev => prev.filter(conv => conv.id !== id));
    }
  };

  const handleExportConversation = (conversation) => {
    const dataStr = JSON.stringify(conversation, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);

    const exportFileDefaultName = `conversation-${conversation.id}-${conversation.date}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const filteredConversations = conversations
    .filter(conv => {
      const matchesSearch = conv.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           conv.preview.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesDate = !selectedDate || conv.date === selectedDate;
      return matchesSearch && matchesDate;
    })
    .sort((a, b) => {
      if (sortBy === 'newest') {
        return new Date(b.timestamp) - new Date(a.timestamp);
      } else if (sortBy === 'oldest') {
        return new Date(a.timestamp) - new Date(b.timestamp);
      } else if (sortBy === 'title') {
        return a.title.localeCompare(b.title);
      }
      return 0;
    });

  return (
    <div className="h-full bg-gradient-to-br from-teal-400 via-cyan-500 to-blue-600 p-6 overflow-y-auto">
      <div className="max-w-6xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-white mb-4">Chat History</h1>
          <p className="text-white/80">Review and manage your conversation history</p>
        </div>

        <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                placeholder="Search conversations..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
              />
            </div>

            <div className="relative">
              <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
              />
            </div>

            <select
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="title">By Title</option>
            </select>
          </div>

          {selectedDate && (
            <button
              onClick={() => setSelectedDate('')}
              className="mb-4 text-white/80 hover:text-white text-sm underline"
            >
              Clear date filter
            </button>
          )}
        </div>

        <div className="space-y-4">
          {filteredConversations.length === 0 ? (
            <div className="text-center py-12">
              <MessageSquare className="w-16 h-16 mx-auto text-white/50 mb-4" />
              <p className="text-white/80 text-lg">
                {searchTerm || selectedDate ? 'No conversations found matching your criteria.' : 'No conversation history yet.'}
              </p>
            </div>
          ) : (
            filteredConversations.map((conversation) => (
              <div key={conversation.id} className="bg-white rounded-lg p-6 shadow-md hover:shadow-lg transition-shadow">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <MessageSquare className="w-5 h-5 text-teal-600" />
                      <h3 className="text-lg font-semibold text-gray-800">{conversation.title}</h3>
                    </div>
                    <p className="text-gray-600 text-sm mb-3">{conversation.preview}</p>
                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      <span className="flex items-center space-x-1">
                        <Calendar className="w-4 h-4" />
                        <span>{new Date(conversation.date).toLocaleDateString()}</span>
                      </span>
                      <span className="flex items-center space-x-1">
                        <MessageSquare className="w-4 h-4" />
                        <span>{conversation.messageCount} messages</span>
                      </span>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2 ml-4">
                    <button
                      onClick={() => handleExportConversation(conversation)}
                      className="p-2 text-gray-500 hover:text-teal-600 hover:bg-teal-50 rounded-lg transition-colors"
                      title="Export conversation"
                    >
                      <Download className="w-4 h-4" />
                    </button>
                    <button
                      onClick={() => handleDeleteConversation(conversation.id)}
                      className="p-2 text-gray-500 hover:text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                      title="Delete conversation"
                    >
                      <Trash2 className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>

        {filteredConversations.length > 0 && (
          <div className="mt-8 text-center">
            <p className="text-white/60 text-sm">
              Showing {filteredConversations.length} of {conversations.length} conversations
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatHistory;
