import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useLanguage } from '../contexts/LanguageContext';
import FeedbackForm from './FeedbackForm';
import { 
  Settings as SettingsIcon, 
  Save, 
  RefreshCw, 
  Shield, 
  Bell, 
  Palette, 
  Database,
  User,
  Lock,
  Globe,
  Eye,
  Zap,
  Monitor,
  Volume2,
  Moon,
  Sun,
  Smartphone,
  Mail,
  AlertTriangle,
  Key,
  Wifi,
  Server
} from 'lucide-react';

const Settings: React.FC = () => {
  const { language, setLanguage, t } = useLanguage();
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [settings, setSettings] = useState({
    // General Settings
    botName: 'SateCha AI Assistant',
    welcomeMessage: 'Welcome to SateCha Cybersecurity Platform!',
    maxResponseLength: 500,

    // API Settings
    deepseekApiKey: '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',
    apiTimeout: 30,

    // UI Settings
    theme: 'dark',
    language: 'en',
    showTypingIndicator: true,
    enableSounds: true,
    enableAnimations: true,

    // Security Settings
    saveConversations: true,
    anonymizeData: true,
    enableEncryption: true,
    autoLogout: 30,

    // Notifications
    emailNotifications: true,
    pushNotifications: true,
    securityAlerts: true,
    threatUpdates: true
  });

  const [activeTab, setActiveTab] = useState('general');
  const [isSaving, setIsSaving] = useState(false);

  const handleInputChange = (key: string, value: string | number | boolean) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const handleSave = async () => {
    setIsSaving(true);
    // Simulate API call to save settings
    await new Promise(resolve => setTimeout(resolve, 1500));
    setIsSaving(false);
    // Show success notification
  };

  const handleReset = () => {
    if (window.confirm('Are you sure you want to reset all settings to default?')) {
      setSettings({
        botName: 'SateCha AI Assistant',
        welcomeMessage: 'Welcome to SateCha Cybersecurity Platform!',
        maxResponseLength: 500,
        deepseekApiKey: '',
        apiTimeout: 30,
        theme: 'dark',
        language: 'en',
        showTypingIndicator: true,
        enableSounds: true,
        enableAnimations: true,
        saveConversations: true,
        anonymizeData: true,
        enableEncryption: true,
        autoLogout: 30,
        emailNotifications: true,
        pushNotifications: true,
        securityAlerts: true,
        threatUpdates: true
      });
    }
  };

  const tabs = [
    { id: 'general', label: 'General', icon: SettingsIcon, color: 'from-cyan-500 to-blue-500' },
    { id: 'security', label: 'Security', icon: Shield, color: 'from-red-500 to-pink-500' },
    { id: 'api', label: 'API', icon: Server, color: 'from-green-500 to-emerald-500' },
    { id: 'ui', label: 'Interface', icon: Monitor, color: 'from-purple-500 to-indigo-500' },
    { id: 'notifications', label: 'Notifications', icon: Bell, color: 'from-yellow-500 to-orange-500' }
  ];

  const TabButton = ({ tab }: { tab: any }) => {
    const Icon = tab.icon;
    return (
      <motion.button
        whileHover={{ scale: 1.05, y: -2 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => setActiveTab(tab.id)}
        className={`relative p-4 rounded-xl font-semibold transition-all duration-300 flex items-center space-x-3 ${
          activeTab === tab.id
            ? `bg-gradient-to-r ${tab.color} text-white shadow-lg`
            : 'glass-button text-gray-300 hover:text-white border border-gray-600 hover:border-cyan-400/50'
        }`}
      >
        <motion.div
          animate={activeTab === tab.id ? { rotate: [0, 360] } : {}}
          transition={{ duration: 2, repeat: activeTab === tab.id ? Infinity : 0, ease: "linear" }}
        >
          <Icon className="w-5 h-5" />
        </motion.div>
        <span className="hidden md:block">{tab.label}</span>

        {activeTab === tab.id && (
          <motion.div
            className="absolute inset-0 rounded-xl border-2 border-white/30"
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            transition={{ duration: 0.3 }}
          />
        )}
      </motion.button>
    );
  };

  const InputField: React.FC<{
    label: string;
    value: string | number;
    onChange: (value: string | number) => void;
    type?: string;
    placeholder?: string;
    icon?: React.ComponentType<any>;
  }> = ({ label, value, onChange, type = 'text', placeholder, icon: Icon }) => (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="mb-6"
    >
      <label className="block text-white font-semibold mb-3 flex items-center">
        {Icon && <Icon className="w-4 h-4 mr-2 text-cyan-400" />}
        {label}
      </label>
      <input
        type={type}
        value={value}
        onChange={(e) => onChange(type === 'number' ? Number(e.target.value) : e.target.value)}
        placeholder={placeholder}
        className="w-full glass-button bg-gray-800/50 text-white px-4 py-3 rounded-lg border border-gray-600 focus:border-cyan-400 focus:outline-none transition-all duration-300"
      />
    </motion.div>
  );

  const ToggleField: React.FC<{
    label: string;
    description?: string;
    value: boolean;
    onChange: (value: boolean) => void;
    icon?: React.ComponentType<any>;
  }> = ({ label, description, value, onChange, icon: Icon }) => (
    <motion.div
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      className="flex items-center justify-between py-4 px-4 glass-card bg-gray-800/30 rounded-lg mb-4"
    >
      <div className="flex items-center">
        {Icon && <Icon className="w-5 h-5 mr-3 text-cyan-400" />}
        <div>
          <p className="font-semibold text-white">{label}</p>
          {description && <p className="text-sm text-gray-400">{description}</p>}
        </div>
      </div>
      <motion.button
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        onClick={() => onChange(!value)}
        className={`relative inline-flex h-7 w-12 items-center rounded-full transition-colors duration-300 ${
          value ? 'bg-gradient-to-r from-cyan-500 to-blue-500' : 'bg-gray-600'
        }`}
      >
        <motion.span
          animate={{ x: value ? 20 : 4 }}
          transition={{ type: "spring", stiffness: 500, damping: 30 }}
          className="inline-block h-5 w-5 transform rounded-full bg-white shadow-lg"
        />
      </motion.button>
    </motion.div>
  );

  return (
    <div className="min-h-screen p-6 overflow-auto">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center mb-8"
        >
          <h1 className="text-4xl font-bold text-white mb-4">
            System Settings
          </h1>
          <p className="text-gray-300 text-lg">
            Configure your SateCha cybersecurity platform
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-4 gap-8">
          {/* Sidebar Navigation */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            className="lg:col-span-1"
          >
            <div className="glass-card p-4 space-y-3">
              <h3 className="text-lg font-bold text-white mb-4 flex items-center">
                <SettingsIcon className="w-5 h-5 mr-2 text-cyan-400" />
                Categories
              </h3>
              {tabs.map((tab, index) => (
                <motion.div
                  key={tab.id}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.1 }}
                >
                  <TabButton tab={tab} />
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Main Content */}
          <div className="lg:col-span-3">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="glass-card p-6"
            >
              <AnimatePresence mode="wait">
                <motion.div
                  key={activeTab}
                  initial={{ opacity: 0, x: 20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -20 }}
                  transition={{ duration: 0.3 }}
                >
                  {activeTab === 'general' && (
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                        <SettingsIcon className="w-6 h-6 mr-3 text-cyan-400" />
                        General Settings
                      </h3>
                      <InputField
                        label="Bot Name"
                        value={settings.botName}
                        onChange={(value) => handleInputChange('botName', value)}
                        placeholder="Enter bot name"
                        icon={User}
                      />
                      <InputField
                        label="Welcome Message"
                        value={settings.welcomeMessage}
                        onChange={(value) => handleInputChange('welcomeMessage', value)}
                        placeholder="Enter welcome message"
                        icon={Mail}
                      />
                      <InputField
                        label="Max Response Length"
                        value={settings.maxResponseLength}
                        onChange={(value) => handleInputChange('maxResponseLength', value)}
                        type="number"
                        icon={AlertTriangle}
                      />
                    </div>
                  )}

                  {activeTab === 'security' && (
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                        <Shield className="w-6 h-6 mr-3 text-red-400" />
                        Security Settings
                      </h3>
                      <ToggleField
                        label="Save Conversations"
                        description="Store chat history for future reference"
                        value={settings.saveConversations}
                        onChange={(value) => handleInputChange('saveConversations', value)}
                        icon={Database}
                      />
                      <ToggleField
                        label="Anonymize Data"
                        description="Remove personal identifiers from stored data"
                        value={settings.anonymizeData}
                        onChange={(value) => handleInputChange('anonymizeData', value)}
                        icon={Eye}
                      />
                      <ToggleField
                        label="Enable Encryption"
                        description="Encrypt all stored data and communications"
                        value={settings.enableEncryption}
                        onChange={(value) => handleInputChange('enableEncryption', value)}
                        icon={Lock}
                      />
                      <InputField
                        label="Auto Logout (minutes)"
                        value={settings.autoLogout}
                        onChange={(value) => handleInputChange('autoLogout', value)}
                        type="number"
                        icon={Key}
                      />
                    </div>
                  )}

                  {activeTab === 'api' && (
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                        <Server className="w-6 h-6 mr-3 text-green-400" />
                        API Settings
                      </h3>
                      <InputField
                        label="DeepSeek API Key"
                        value={settings.deepseekApiKey}
                        onChange={(value) => handleInputChange('deepseekApiKey', value)}
                        type="password"
                        placeholder="Enter your API key"
                        icon={Key}
                      />
                      <InputField
                        label="API Timeout (seconds)"
                        value={settings.apiTimeout}
                        onChange={(value) => handleInputChange('apiTimeout', value)}
                        type="number"
                        icon={Zap}
                      />
                    </div>
                  )}

                  {activeTab === 'ui' && (
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                        <Monitor className="w-6 h-6 mr-3 text-purple-400" />
                        Interface Settings
                      </h3>
                      <div className="mb-6">
                        <label className="block text-white font-semibold mb-3 flex items-center">
                          <Palette className="w-4 h-4 mr-2 text-cyan-400" />
                          Theme
                        </label>
                        <div className="grid grid-cols-2 gap-4">
                          {[
                            { value: 'dark', label: 'Dark', icon: Moon },
                            { value: 'light', label: 'Light', icon: Sun }
                          ].map((theme) => {
                            const Icon = theme.icon;
                            return (
                              <motion.button
                                key={theme.value}
                                whileHover={{ scale: 1.05 }}
                                whileTap={{ scale: 0.95 }}
                                onClick={() => handleInputChange('theme', theme.value)}
                                className={`p-4 rounded-lg border-2 transition-all duration-300 flex items-center space-x-3 ${
                                  settings.theme === theme.value
                                    ? 'bg-gradient-to-r from-purple-500 to-indigo-500 border-purple-400 text-white'
                                    : 'glass-button border-gray-600 text-gray-300 hover:border-purple-400'
                                }`}
                              >
                                <Icon className="w-5 h-5" />
                                <span>{theme.label}</span>
                              </motion.button>
                            );
                          })}
                        </div>
                      </div>
                      <ToggleField
                        label="Show Typing Indicator"
                        description="Display typing animation when bot is responding"
                        value={settings.showTypingIndicator}
                        onChange={(value) => handleInputChange('showTypingIndicator', value)}
                        icon={Zap}
                      />
                      <ToggleField
                        label="Enable Sounds"
                        description="Play notification sounds for new messages"
                        value={settings.enableSounds}
                        onChange={(value) => handleInputChange('enableSounds', value)}
                        icon={Volume2}
                      />
                      <ToggleField
                        label="Enable Animations"
                        description="Show smooth animations and transitions"
                        value={settings.enableAnimations}
                        onChange={(value) => handleInputChange('enableAnimations', value)}
                        icon={Zap}
                      />
                    </div>
                  )}

                  {activeTab === 'notifications' && (
                    <div>
                      <h3 className="text-2xl font-bold text-white mb-6 flex items-center">
                        <Bell className="w-6 h-6 mr-3 text-yellow-400" />
                        Notification Settings
                      </h3>
                      <ToggleField
                        label="Email Notifications"
                        description="Receive email updates about important events"
                        value={settings.emailNotifications}
                        onChange={(value) => handleInputChange('emailNotifications', value)}
                        icon={Mail}
                      />
                      <ToggleField
                        label="Push Notifications"
                        description="Receive browser push notifications"
                        value={settings.pushNotifications}
                        onChange={(value) => handleInputChange('pushNotifications', value)}
                        icon={Smartphone}
                      />
                      <ToggleField
                        label="Security Alerts"
                        description="Get notified about security threats and incidents"
                        value={settings.securityAlerts}
                        onChange={(value) => handleInputChange('securityAlerts', value)}
                        icon={Shield}
                      />
                      <ToggleField
                        label="Threat Updates"
                        description="Receive updates about new cybersecurity threats"
                        value={settings.threatUpdates}
                        onChange={(value) => handleInputChange('threatUpdates', value)}
                        icon={AlertTriangle}
                      />
                    </div>
                  )}
                </motion.div>
              </AnimatePresence>

              {/* Action Buttons */}
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.3 }}
                className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4 mt-8 pt-6 border-t border-gray-600"
              >
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={handleSave}
                  disabled={isSaving}
                  className="flex items-center justify-center space-x-2 bg-gradient-to-r from-cyan-500 to-blue-500 text-white px-8 py-3 rounded-xl font-semibold disabled:opacity-50 transition-all duration-300"
                >
                  {isSaving ? (
                    <motion.div
                      animate={{ rotate: 360 }}
                      transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                    >
                      <RefreshCw className="w-5 h-5" />
                    </motion.div>
                  ) : (
                    <Save className="w-5 h-5" />
                  )}
                  <span>{isSaving ? 'Saving...' : 'Save Changes'}</span>
                </motion.button>

                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={handleReset}
                  className="flex items-center justify-center space-x-2 glass-button bg-gradient-to-r from-red-500 to-pink-500 text-white px-8 py-3 rounded-xl font-semibold"
                >
                  <RefreshCw className="w-5 h-5" />
                  <span>Reset to Default</span>
                </motion.button>
              </motion.div>
            </motion.div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Settings;
