import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, User, BarChart3, HelpCircle, Settings as SettingsIcon, Wrench, LogIn, LogOut } from 'lucide-react';
import './styles/globals.css';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import Quiz from './components/Quiz';
import Tools from './components/Tools';
import Settings from './components/Settings';
import Profile from './components/Profile';
import AuthModal from './components/AuthModal';
import { AuthProvider, useAuth } from './contexts/AuthContext';

const AppContent: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'home' | 'dashboard' | 'quiz' | 'tools' | 'settings' | 'profile'>('home');
  const [isLoading, setIsLoading] = useState(true);
  const [particles, setParticles] = useState<Array<{id: number, x: number, y: number, delay: number}>>([]);
  const [showAuthModal, setShowAuthModal] = useState(false);
  const { user, signOut } = useAuth();

  useEffect(() => {
    // Generate floating particles for background
    const newParticles = Array.from({ length: 30 }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      delay: Math.random() * 2
    }));
    setParticles(newParticles);

    // Simulate loading
    setTimeout(() => setIsLoading(false), 1500);
  }, []);

  const navItems = [
    { id: 'dashboard', label: 'Dashboard', icon: BarChart3 },
    { id: 'quiz', label: 'Quiz', icon: HelpCircle },
    { id: 'tools', label: 'Tools', icon: Wrench },
    { id: 'settings', label: 'Settings', icon: SettingsIcon }
  ];

  if (isLoading) {
    return (
      <div className="h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 flex items-center justify-center overflow-hidden cyber-grid">
        <motion.div
          initial={{ opacity: 0, scale: 0.5 }}
          animate={{ opacity: 1, scale: 1 }}
          className="text-center glass-card"
        >
          <motion.div className="flex items-center justify-center mb-6">
            <motion.img
              src="/logo.png"
              alt="SateCha Logo"
              className="w-16 h-16 mr-4"
              animate={{ scale: [1, 1.05, 1] }}
              transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            />
            <div>
              <motion.h1
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.3 }}
                className="text-4xl font-bold text-white"
                style={{ fontFamily: 'Orbitron, monospace' }}
              >
                SateCha
              </motion.h1>
              <motion.p
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.5 }}
                className="text-cyan-400 text-sm"
              >
                Cybersecurity Platform
              </motion.p>
            </div>
          </motion.div>

          <motion.div
            animate={{ scale: [1, 1.1, 1], opacity: [0.7, 1, 0.7] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
            className="cyber-spinner mx-auto mb-4"
          />

          <motion.p
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.7 }}
            className="text-gray-300"
          >
            Initializing Security Systems...
          </motion.p>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 text-white flex flex-col relative overflow-hidden cyber-grid">
      {/* Animated Background */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating Particles */}
        {particles.map((particle) => (
          <motion.div
            key={particle.id}
            className="particle"
            style={{ left: `${particle.x}%`, top: `${particle.y}%` }}
            animate={{
              y: [0, -100, 0],
              opacity: [0, 1, 0],
              scale: [0, 1, 0]
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              delay: particle.delay,
              ease: "easeInOut"
            }}
          />
        ))}
      </div>

      {/* Header Navigation */}
      <motion.header
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="relative z-10 glass-dark border-b border-cyan-500/20"
      >
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <motion.div
              whileHover={{ scale: 1.05 }}
              className="flex items-center space-x-3 cursor-pointer"
              onClick={() => setActiveTab('home')}
            >
              <motion.div
                whileHover={{ scale: 1.1 }}
                className="relative"
              >
                <img
                  src="/logo.png"
                  alt="SateCha Logo"
                  className="w-12 h-12 rounded-lg"
                />
                <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-lg blur-lg opacity-30 -z-10" />
              </motion.div>
              <div>
                <h1 className="text-2xl font-bold text-white" style={{ fontFamily: 'Orbitron, monospace' }}>
                  SateCha
                </h1>
                <p className="text-xs text-cyan-400">Cybersecurity Platform</p>
              </div>
            </motion.div>

            {/* Navigation */}
            <nav className="flex space-x-1">
              {navItems.map((item, index) => {
                const Icon = item.icon;
                const gradients = [
                  'from-cyan-500 via-blue-500 to-purple-600',
                  'from-green-400 via-cyan-500 to-blue-600', 
                  'from-purple-500 via-pink-500 to-red-500',
                  'from-yellow-400 via-orange-500 to-red-600'
                ];
                const glowColors = [
                  'shadow-cyan-500/50',
                  'shadow-green-500/50',
                  'shadow-purple-500/50', 
                  'shadow-yellow-500/50'
                ];
                return (
                  <motion.button
                    key={item.id}
                    initial={{ opacity: 0, y: -30, rotateX: -90 }}
                    animate={{ opacity: 1, y: 0, rotateX: 0 }}
                    transition={{ 
                      delay: index * 0.15 + 0.3,
                      type: "spring",
                      stiffness: 100,
                      damping: 10
                    }}
                    whileHover={{ 
                      scale: 1.1, 
                      y: -5,
                      rotateY: 5,
                      transition: { duration: 0.2 }
                    }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => setActiveTab(item.id as any)}
                    className={`relative px-4 py-3 rounded-xl font-semibold transition-all duration-500 flex items-center space-x-2 overflow-hidden group ${
                      activeTab === item.id
                        ? `bg-gradient-to-r ${gradients[index % gradients.length]} text-white shadow-lg ${glowColors[index % glowColors.length]}`
                        : 'glass-button text-gray-300 hover:text-white border border-cyan-500/20 hover:border-cyan-400/50'
                    }`}
                  >
                    {/* Animated background for inactive buttons */}
                    {activeTab !== item.id && (
                      <motion.div
                        className={`absolute inset-0 bg-gradient-to-r ${gradients[index % gradients.length]} opacity-0 group-hover:opacity-20 transition-opacity duration-300`}
                        initial={false}
                      />
                    )}

                    {/* Animated border effect */}
                    <motion.div
                      className="absolute inset-0 rounded-xl"
                      style={{
                        background: `linear-gradient(45deg, transparent, ${activeTab === item.id ? 'rgba(255,255,255,0.3)' : 'rgba(0,255,255,0.2)'}, transparent)`,
                        backgroundSize: '200% 200%'
                      }}
                      animate={{
                        backgroundPosition: ['0% 0%', '100% 100%', '0% 0%']
                      }}
                      transition={{
                        duration: 3,
                        repeat: Infinity,
                        ease: "linear"
                      }}
                    />

                    <motion.div
                      animate={activeTab === item.id ? {
                        scale: [1, 1.1, 1]
                      } : {}}
                      transition={{
                        duration: 1.5,
                        repeat: activeTab === item.id ? Infinity : 0,
                        ease: "easeInOut"
                      }}
                    >
                      <Icon className="w-5 h-5 relative z-10" />
                    </motion.div>
                    <span className="hidden md:block relative z-10 font-bold">{item.label}</span>

                    {/* Particle effect for active tab */}
                    {activeTab === item.id && (
                      <motion.div
                        className="absolute inset-0 pointer-events-none"
                        initial={false}
                      >
                        {[...Array(6)].map((_, i) => (
                          <motion.div
                            key={i}
                            className="absolute w-1 h-1 bg-white rounded-full"
                            style={{
                              left: `${20 + i * 10}%`,
                              top: `${30 + (i % 2) * 40}%`
                            }}
                            animate={{
                              y: [-10, -20, -10],
                              opacity: [0, 1, 0],
                              scale: [0, 1, 0]
                            }}
                            transition={{
                              duration: 2,
                              repeat: Infinity,
                              delay: i * 0.2,
                              ease: "easeInOut"
                            }}
                          />
                        ))}
                      </motion.div>
                    )}
                  </motion.button>
                );
              })}
            </nav>

            {/* Authentication & Profile */}
            <div className="flex items-center space-x-3">
              {user ? (
                <>
                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => setActiveTab('profile')}
                    className="relative group"
                  >
                    <div className="w-10 h-10 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full flex items-center justify-center">
                      <User className="w-5 h-5 text-black" />
                    </div>
                    <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-full blur-lg opacity-0 group-hover:opacity-50 transition-opacity -z-10" />
                  </motion.button>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={signOut}
                    className="px-3 py-2 bg-red-500/20 text-red-400 border border-red-500/30 rounded-lg hover:bg-red-500/30 transition-colors flex items-center space-x-2"
                  >
                    <LogOut className="w-4 h-4" />
                    <span className="hidden sm:block text-sm">Sign Out</span>
                  </motion.button>
                </>
              ) : (
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => setShowAuthModal(true)}
                  className="px-4 py-2 bg-gradient-to-r from-cyan-500 to-blue-500 text-white rounded-lg hover:from-cyan-400 hover:to-blue-400 transition-colors flex items-center space-x-2 font-orbitron"
                >
                  <LogIn className="w-4 h-4" />
                  <span>Sign In</span>
                </motion.button>
              )}
            </div>
          </div>
        </div>
      </motion.header>

      {/* Main Content */}
      <main className="flex-1 overflow-auto relative z-10">
        <AnimatePresence mode="wait">
          <motion.div
            key={activeTab}
            initial={{ opacity: 0, x: 300, scale: 0.9 }}
            animate={{ opacity: 1, x: 0, scale: 1 }}
            exit={{ opacity: 0, x: -300, scale: 0.9 }}
            transition={{ duration: 0.4, ease: "easeInOut" }}
            className="h-full"
          >
            {activeTab === 'home' && <Home />}
            {activeTab === 'dashboard' && <Dashboard />}
            {activeTab === 'quiz' && <Quiz />}
            {activeTab === 'tools' && <Tools />}
            {activeTab === 'settings' && <Settings />}
            {activeTab === 'profile' && <Profile />}
          </motion.div>
        </AnimatePresence>
      </main>

      {/* Tech Border Effects */}
      <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-cyan-400 to-transparent" />
      <div className="absolute bottom-0 left-0 w-full h-1 bg-gradient-to-r from-transparent via-blue-500 to-transparent" />
      <div className="absolute top-0 left-0 w-1 h-full bg-gradient-to-b from-transparent via-cyan-400 to-transparent" />
      <div className="absolute top-0 right-0 w-1 h-full bg-gradient-to-b from-transparent via-blue-500 to-transparent" />

      {/* Authentication Modal */}
      <AuthModal 
        isOpen={showAuthModal} 
        onClose={() => setShowAuthModal(false)} 
      />
    </div>
  );
};

// Main App component with AuthProvider
function App() {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
}

export default App;
