import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react';
import { supabase } from '../lib/supabase';
import type { User } from '@supabase/supabase-js';

interface AuthContextType {
  user: User | null;
  loading: boolean;
  sendOTP: (email: string, mode: 'signup' | 'signin', userData?: {
    username: string;
    fullName: string;
    languagePreference: 'en' | 'my';
  }) => Promise<{ error?: string }>;
  verifyOTP: (email: string, otp: string, mode: 'signup' | 'signin') => Promise<{ error?: string }>;
  signOut: () => Promise<void>;
  updateProfile: (updates: any) => Promise<{ error?: string }>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

interface AuthProviderProps {
  children: ReactNode;
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for existing session
    const initializeAuth = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        setUser(user);
      } catch (error) {
        console.error('Auth initialization error:', error);
      } finally {
        setLoading(false);
      }
    };

    initializeAuth();

    // Listen for auth changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange((event, session) => {
      setUser(session?.user ?? null);
      setLoading(false);
    });

    return () => {
      subscription?.unsubscribe();
    };
  }, []);

  const sendOTP = async (email: string, mode: 'signup' | 'signin', userData?: {
    username: string;
    fullName: string;
    languagePreference: 'en' | 'my';
  }) => {
    setLoading(true);
    try {
      if (mode === 'signup') {
        // For signup, use signUp with email only to trigger OTP
        const { error } = await supabase.auth.signUp({
          email,
          password: 'temp-password', // Supabase requires password, but we'll use OTP
          options: {
            data: userData || {},
            emailRedirectTo: undefined // Disable email confirmation redirect
          }
        });
        if (error) {
          return { error: error.message };
        }
      } else {
        // For signin, use signInWithOtp
        const { error } = await supabase.auth.signInWithOtp({
          email,
          options: {
            shouldCreateUser: false
          }
        });
        if (error) {
          return { error: error.message };
        }
      }
      return {};
    } catch (error) {
      return { error: 'Failed to send OTP' };
    } finally {
      setLoading(false);
    }
  };

  const verifyOTP = async (email: string, otp: string, mode: 'signup' | 'signin') => {
    setLoading(true);
    try {
      const { data, error } = await supabase.auth.verifyOtp({
        email,
        token: otp,
        type: mode === 'signup' ? 'signup' : 'email'
      });

      if (error) {
        return { error: error.message };
      }

      if (data.user) {
        setUser(data.user);
        return {};
      }

      return { error: 'OTP verification failed' };
    } catch (error) {
      return { error: 'An unexpected error occurred' };
    } finally {
      setLoading(false);
    }
  };

  const signOut = async () => {
    setLoading(true);
    try {
      await supabase.auth.signOut();
      setUser(null);
    } catch (error) {
      console.error('Sign out error:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateProfile = async (updates: any) => {
    if (!user) return { error: 'No user logged in' };

    try {
      const { data, error } = await supabase.auth.updateUser({
        data: updates
      });

      if (error) {
        return { error: error.message };
      }

      if (data.user) {
        setUser(data.user);
      }

      return {};
    } catch (error) {
      return { error: 'Failed to update profile' };
    }
  };

  const value: AuthContextType = {
    user,
    loading,
    signIn,
    signUp,
    signOut,
    updateProfile,
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
