import React, { useState } from 'react';
import { Shield, MessageSquare, BarChart3, Settings as SettingsIcon, User, HelpCircle } from 'lucide-react';
import Chat from './components/Chat.js';
import KnowledgeBase from './components/KnowledgeBase.js';
import Dashboard from './components/Dashboard';
import Settings from './components/Settings.js';
import ChatHistory from './components/ChatHistory';
import Help from './components/Help';

function App() {
  const [activeTab, setActiveTab] = useState<'dashboard' | 'chat' | 'quiz' | 'tools' | 'settings' | 'profile'>('dashboard');

  return (
    <div className="h-screen bg-gradient-to-br from-teal-400 via-cyan-500 to-blue-600 flex flex-col">
      {/* Header Navigation - SateCha Style */}
      <header className="bg-teal-800/90 backdrop-blur-sm shadow-lg">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            {/* Logo */}
            <div className="flex items-center space-x-3">
              <div className="bg-white/20 p-2 rounded-lg">
                <Shield className="w-8 h-8 text-white" />
              </div>
              <h1 className="text-2xl font-bold text-white">SateCha</h1>
            </div>

            {/* Navigation */}
            <nav className="flex space-x-1">
              <button
                onClick={() => setActiveTab('dashboard')}
                className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                  activeTab === 'dashboard'
                    ? 'bg-white/20 text-white shadow-lg'
                    : 'text-white/80 hover:bg-white/10 hover:text-white'
                }`}
              >
                Dashboard
              </button>
              <button
                onClick={() => setActiveTab('quiz')}
                className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                  activeTab === 'quiz'
                    ? 'bg-white/20 text-white shadow-lg'
                    : 'text-white/80 hover:bg-white/10 hover:text-white'
                }`}
              >
                Quiz
              </button>
              <button
                onClick={() => setActiveTab('tools')}
                className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                  activeTab === 'tools'
                    ? 'bg-white/20 text-white shadow-lg'
                    : 'text-white/80 hover:bg-white/10 hover:text-white'
                }`}
              >
                Tools
              </button>
              <button
                onClick={() => setActiveTab('settings')}
                className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                  activeTab === 'settings'
                    ? 'bg-white/20 text-white shadow-lg'
                    : 'text-white/80 hover:bg-white/10 hover:text-white'
                }`}
              >
                Settings
              </button>
            </nav>

            {/* Profile */}
            <button
              onClick={() => setActiveTab('profile')}
              className="bg-white/20 p-2 rounded-full hover:bg-white/30 transition-colors"
            >
              <User className="w-6 h-6 text-white" />
            </button>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="flex-1 overflow-hidden">
        <div className="h-full">
          {activeTab === 'dashboard' && <Dashboard />}
          {activeTab === 'chat' && <Chat />}
          {activeTab === 'quiz' && <KnowledgeBase />}
          {activeTab === 'tools' && <ChatHistory />}
          {activeTab === 'settings' && <Settings />}
          {activeTab === 'profile' && <Help />}
        </div>
      </main>
    </div>
  );
}

export default App;
